import React,{ Fragment, useContext, useState, useEffect }  from 'react';
import {nanoid} from "nanoid"

export default function Tile(props){
    const [notesList, setNotesList] = useState(createNotesList())

    function createNotesList (){
        if(JSON.parse(localStorage.getItem("notes" + props.id))){
            var list = JSON.parse(localStorage.getItem("notes" + props.id))
            return setNotesList(() => list.map(oldList => oldList.value))
        }
        else return []
    }

    if(!props.note || props.note === ''){
        setNotesList(() => [])
        localStorage.setItem("notes" + props.id, JSON.stringify([]))
    }
    else{
        setNotesList(oldList => [...new Set([...oldList, props.note])])
        var notes = notesList.map(note => {value: note})
        localStorage.setItem("notes" + props.id, JSON.stringify(notes))
    }

     let name
        if ((props.c===2 || props.c===5) && (props.r===2 || props.r===5)) {
            name = "singleTileThickCR"
        } else if (props.c===2 || props.c===5) {
            name = "singleTileThickC"
        } else if (props.r===2 || props.r===5){
            name = "singleTileThickR"
        } else {
            name = "singleTile"
        }
    const highligh = {
        backgroundColor: props.highligh? "#59E391" : "white"
    }

    return(
        <button className= {name} onClick = {props.fillTile} >
            {!props.notesMode && <h2 className="number" > {props.value === 0 ? "": props.value} </h2>}
            <div>
            {props.notesMode && <h4 className="notes"> {notesJs} </h4>}
            </div>
         </button>
    )
}
