import React from 'react';
import Tile from './Tile';
import Number,{useCountContext,useCountUpdateContext} from './Number';
import data from "./data"
import {nanoid} from "nanoid"
import Confetti from "react-confetti"

export default function Game() {
    const [sudoku, setSudoku] = React.useState(generateSudokuGrid())
    const [numbers, setNumbers] = React.useState(allNumbers())
    const [selectNum, setSelectNum] = React.useState()
    const [notesMode, setNotesMode] = React.useState(false)
    const count = useCountContext();
    const CountUpdate = useCountUpdateContext()

    const sudokuGrid = sudoku.map(s =>{
        return(
            <Tile
                id={s.id}
                number = {s.number}
                r = {s.r}
                c = {s.c}
                isInitValue={s.isInitValue}
                fillTile = {() => fillTile(s.id)}
                notesMode = {s.notesMode}
                note = {s.note}
            />)
            })
    function fillTile(id){
        setSudoku(oldSudoku => oldSudoku.map(s => {
            return s.id === id ?
                {...s, number: selectNum,  notesMode: notesMode, note : [...s.note, selectNum] } : s
        }))
    }

       const rowNumber = numbers.map(n => {
        return(<Number
                number={n.value}
                isSelected={n.isSelected}
                id={n.id}
                count={n.count}
                selectNumber= {() => setSelectNum(n.value)}
                />)
    })

    function generateSudokuGrid() {
        const grid = []
            data.map(sudoku => {
                    (
                        sudoku.schema.map(row =>{
                            (
                                row.row.map(tile =>{
                                    grid.push({
                                            number: tile.n ,
                                            r: sudoku.schema.indexOf(row),
                                            c: row.row.indexOf(tile),
                                            id: nanoid(),
                                            isInitValue: (tile.n === "")? false : true,
                                            note: [],
                                            })
                                })
                            )
                        })
                    )
                })
          return grid
      }
      function allNumbers() {
        const numbers = []
        for (let i = 0; i < 10; i++) {
            numbers.push(createNumber(i))
        }
        return numbers
    }

    function createNumber(i) {
        return {
            value: ((i === 0)? "": i),
            isSelected: false,
            id: nanoid(),
            count: sudoku.filter((s) => s.number === i).length
            }
        }
        function addNotes(){
            setNotesMode(old => !old)
        }
    return (
        <main>
            <div className= "sudoku-grid">
                {sudokuGrid}
            </div>
            <div className= "num-grid">
                {rowNumber}
            </div>
            <button onClick={addNotes} >{notesMode ? "notes" : "write"}</button>
        </main>
    )
}
