import React, {useRef, useEffect, useState} from 'react';
import Tile from './Tile';
import {nanoid} from "nanoid"
import Confetti from "react-confetti"
import Number from './Number'
import {generateSudokuGrid, allNumbers,checkWin} from './SudokuApi'

export default function Game() {
    const [sudoku, setSudoku] = useState(generateSudokuGrid())
    const [numbers, setNumbers] = useState(allNumbers())
    const [selectTile, setSelectTile] = useState('')
    const [notesMode, setNotesMode] = useState(false)
    const [winGame, setWinGame] = useState(false)
    const prevSelectTile = useRef({
        value: '',
        r: '',
        c: '',
        id: '',
        isNotInitValue: false,
        note: []
    })


    const sudokuGrid = sudoku.map(s =>{
        return(
            <Tile
                id={s.id}
                value = {s.value}
                r = {s.r}
                c = {s.c}
                isNotInitValue={s.isNotInitValue}
                fillTile = {() => setSelectTile(s)}
                notesMode = {s.notesMode}
                note = {s.note}
            />)
        })
    const rowNumber = numbers.map(n => {
        return(<Number
                number={n.value}
                id={n.id}
                count={n.count}
                selectNumber= {() => fillTile(n.value)}
                />)
    })

    useEffect(() => {
        prevSelectTile.current = selectTile    
    }, [selectTile])

    useEffect(() => {
        const totCount = sudoku.reduce((perNumber, {value}) => {
            if (!perNumber[value]) perNumber[value] = 1;
            perNumber[value] =  perNumber[value]  + 1
            return perNumber;
          }, {})
          setNumbers(oldNum => oldNum.map(n => {
                return {...n, count: totCount[n.value]} 
            }))       

    }, [sudoku])

    React.useEffect(() => {
        if (numbers[0] == 0 ) {
            if(checkWin(sudoku)){
                setWinGame(true)
                console.log("YOU WON")
            }
        }
    }, [numbers])

    function fillTile(value){
        setSudoku(oldSudoku => oldSudoku.map(s => {
            if(s.id === selectTile.id && s.isNotInitValue) {
                return notesMode?
                {...s, notesMode: notesMode,  note : new Set([...s.note, value]), value: ''} :
                {...s, notesMode: notesMode, value: value, note : new Set()} 
            }
            else
                return s
        }))
    }

    function switchNotesMode(){
        setNotesMode(old => !old)
    }


    return (
        <main>
            <div className= "sudoku-grid">
                {sudokuGrid}
            </div>
            <div className= "num-grid">
                {rowNumber}
            </div>
            <button onClick={switchNotesMode} >{notesMode ? "notes" : "write"}</button>
        </main>
    )
}
