import React from 'react';
import Tile from './components/Tile';
import Number from './components/Number';
import data from "./components/data"
import {nanoid} from "nanoid"

export default function App() {
    const [numbers, setNumbers] = React.useState(allNumbers())
    function selectNumber(id) {
        setNumbers(oldNumber => oldNumber.map(n => {
            return n.id === id ?
                {...n, isSelected: !n.isSelected} : n
        }))
    }


    const numberGrid = numbers.map(n => {
        return(<Number
                value={n.value}
                isSelected={n.isSelected}
                id={n.id}
                selectNumber= {() => selectNumber(n.id)}
                />)
    })

    const grid = data.map(sudoku => {
        return(
            sudoku.schema.map(row =>{
                return(
                    row.row.map(tile =>{
                        return(<Tile number={tile.n} r={sudoku.schema.indexOf(row)} c={row.row.indexOf(tile)}/>)
                    })
                )
            })
        )
    })
        function allNumbers() {
            const numbers = []
            for (let i = 1; i < 10; i++) {
                numbers.push(createNumber(i))
            }
            console.log(numbers)
            return numbers
        }

        function createNumber(i) {
            return {
                value: i,
                isSelected: false,
                id: nanoid()
                }
            }

    return (
        <main>
            <div className= "sudoku-grid">
                {grid}
            </div>
            <div className= "num-grid">
                {numberGrid}
            </div>
        </main>
    )
}
