import React from 'react';
import Tile from './components/Tile';
import Number from './components/Number';
import data from "./components/data"
import {nanoid} from "nanoid"

export default function App() {
    const [sudoku, setSudoku] = React.useState(generateSudokuGrid())
    const [numbers, setNumbers] = React.useState(allNumbers())
    const [squares, setSquares] = React.useState(generateSquares())
    const [rows, setRows] = React.useState(generateRows())
    const [columns, setColumns] = React.useState(generateColumns())
    const [selectedNum, setSelectedNum] = React.useState()
    const [winGame, setWinGame] = React.useState()

    const rowNumber = numbers.map(n => {
        return(<Number
                value={n.value}
                isSelected={n.isSelected}
                id={n.id}
                count={n.count}
                selectNumber= {() => selectNumber(n.id, n.value)}
                />)
    })
    const sudokuGrid = sudoku.map(s =>{
        return(
            <Tile
                id={s.id}
                number = {s.number}
                r = {s.r}
                c = {s.c}
                isInitValue={s.isInitValue}
                insertNum = {() => insertNum((s.id), (s.number))}
            />)
            }
    )


    React.useEffect(() => {
        const allSelected = numbers.every(n => n.count === 9)
        if (allSelected ) {
            const squareCheck = square.every(sq => {
                const uniqueSquare = new Set(sq)
                uniqueSquare.size() === 9
               } )
            const rowCheck = rows.every(row => {
                const uniqueRow = new Set(row)
                uniqueRow.size() === 9
            })
            const columnCheck = columns.every(column => {
                const uniqueColumn = new Set(column)
                uniqueColumn.size() === 9
            })

           if(squareCheck && rowCheck && columnCheck){

           }
        }
    }, [numbers])

    function generateSudokuGrid() {
      const grid = []
          data.map(sudoku => {
                  (
                      sudoku.schema.map(row =>{
                          (
                              row.row.map(tile =>{
                                  grid.push({
                                          number: tile.n ,
                                          r: sudoku.schema.indexOf(row),
                                          c: row.row.indexOf(tile),
                                          id: nanoid(),
                                          isInitValue: (tile.n === "")? false : true
                                          })
                              })
                          )
                      })
                  )
              })
        return grid
    }
    function insertNum(id, value) {
            const isInitValue = sudoku.filter(s => s.id === id)[0].isInitValue
            const isOnBound = numbers.filter(n => n.value === selectedNum)[0].count < 9

            setSudoku(oldSudoku => oldSudoku.map(s => {
                return s.id === id && !s.isInitValue && isOnBound?
                    {...s, number: selectedNum} : s
            }))
        if (value === ""){
            setNumbers(oldNumber => oldNumber.map(n => {
                return n.value === selectedNum  && n.count < 9?
                    {...n, count: (n.count + 1)} : n
          }))}
        else{
        setNumbers(oldNumber => oldNumber.map(n => {
                        if (value === selectedNum || isInitValue)
                            return n
                       else if(n.value === selectedNum && n.count < 9)
                         return {...n, count: (n.count + 1)}
                       else if (n.value === value && n.count > 0)
                        return {...n, count: (n.count - 1)}
                        else
                        return n
                  }))
        }
        }

    function allNumbers() {
            const numbers = []
            for (let i = 0; i < 10; i++) {
                numbers.push(createNumber(i))
            }
            return numbers
        }
    function createNumber(i) {
            return {
                value: ((i === 0)? "": i),
                isSelected: false,
                id: nanoid(),
                count: sudoku.filter((s) => s.number === i).length
                }
            }
    function selectNumber(id,value) {
        setSelectedNum(value)
        setNumbers(oldNumber => oldNumber.map(n => {
            return n.id === id ?
                {...n, isSelected: true} :
                {...n, isSelected: false}
        }))

    }

    function generateSquares(){
        const sq = []
        for(let j = 0; j < 61; j = j + 27){
        for(let i = j; i < j + 8; i = i + 3){
            sq.push ((sudoku.slice(i, i +3).concat
            (sudoku.slice(i + 9, i + 12))).concat
            (sudoku.slice(i +18, i + 21)))
        }
        }
        return sq
    }
    function generateColumns(){
        const columns = []
        for(let i = 0; i < 9; i++){
            columns.push([])
            for(let j = 0; j < 9; j++){
                (columns[i]).push(rows[j][i])
            }
        }
        return columns;
    }
    function generateRows(){
        const rows = []
        for(let i = 0; i < 81; i = i + 9){
            rows.push(sudoku.slice(i, i + 9 ))
        }
        return rows;
    }

    console.log(columns)

    return (
        <main>
            <div className= "sudoku-grid">
                {sudokuGrid}
            </div>
            <div className= "num-grid">
                {rowNumber}
            </div>
            <h2>{selectedNum}</h2>
        </main>
    )
}
