import React, { Fragment } from 'react';
import Tile from './Tile';
import data from "./data"
import {nanoid} from "nanoid"

export function generateSudokuGrid() {
    const grid = []
        data.map(sudoku => {
                (
                    sudoku.schema.map(row =>{
                        (
                            row.row.map(tile =>{
                                grid.push({
                                        value: tile.n ,
                                        r: sudoku.schema.indexOf(row),
                                        c: row.row.indexOf(tile),
                                        id: nanoid(),
                                        isNotInitValue: (tile.n === "")? true : false,
                                        note: []
                                        })
                            })
                        )
                    })
                )
            })
      return grid
  }

export function allNumbers() {
    const numbers = []
    for (let i = 0; i <= 9; i++) {
        numbers.push(createNumber(i))
    }
    return numbers
}

function createNumber(i) {
    return {
        value: i,
        id: nanoid(),
        count: generateSudokuGrid().filter((s) => s.number === i).length
        }
    }
