import React, {Fragment, useRef, useEffect, useState} from 'react';
import {nanoid} from "nanoid"
import {generateSudokuGrids} from './SudokuApi'

export default function Menu(props){
    const [sessions, setSessions] = useState([])
    const [schemes, setSchemes] = useState(generateSudokuGrids())
    const [showSchemes, setShowSchemes] = useState(false)
    const [showSessions, setShowSessions] = useState(false)

    function schemesList(){
        schemes.map(scheme => <h4 className='sudoku-grid'>{scheme}</h4>)
    }

    return(
        <Fragment>
         <h4 className="menu"> Menu </h4>
         <button className = 'continue' onClick ={() => setShowSchemes(true)}>New Game</button>
         <button className = 'continue' onClick ={() => setShowSessions(true)}>Continue</button>
         {showSchemes && {schemes}}
        </Fragment>
    )
}
