import React from 'react';
import Tile from './components/Tile';
import data from "./components/data"
import {nanoid} from "nanoid"

export default function App() {
    const [numbers, setNumbers] = React.useState(allNumbers())
    const numberGrid = numbers.map(n => {
        return(<h2 className= "num">{n}</h2>)
    })

    const grid = data.map(sudoku => {
        return(
            sudoku.schema.map(row =>{
                return(
                    row.row.map(tile =>{
                        return(<Tile number={tile.n} r={sudoku.schema.indexOf(row)} c={row.row.indexOf(tile)}/>)
                    })
                )
            })
        )
    })
        function allNumbers() {
            const numbers = []
            for (let i = 1; i < 10; i++) {
                numbers.push(createNumber(i))
            }
            return numbers
        }

        function createNumber(i) {
            return {
                value: Math.ceil(Math.random() * 6),
                isHeld: false,
                id: nanoid()
            }
        }
    return (
        <main>
            <div className= "sudoku-grid">
                {grid}
            </div>
            <div className= "num-grid">
                {numberGrid}
            </div>
        </main>
    )
}
