import React, { Fragment } from 'react';
import Tile from './Tile';
import data from "./data"
import {nanoid} from "nanoid"

export function generateSudokuGrid() {
    const grid = []
        data.map(sudoku => {
                (
                    sudoku.schema.map(row =>{
                        (
                            row.row.map(tile =>{
                                grid.push({
                                        value: tile.n ,
                                        r: sudoku.schema.indexOf(row),
                                        c: row.row.indexOf(tile),
                                        id: nanoid(),
                                        isNotInitValue: (tile.n === 0)? true : false,
                                        note: new Set()
                                        })
                            })
                        )
                    })
                )
            })
      return grid
  }

export function allNumbers() {
    const numbers = []
    for (let i = 0; i <= 9; i++) {
        numbers.push(createNumber(i))
    }
    return numbers
}

function createNumber(i) {
    return {
        value: i,
        id: nanoid(),
        count: null
        }
    }

    function generateRows(sudoku){
        const rows = []
        for(let i = 0; i < 81; i = i + 9){
            rows.push(sudoku.slice(i, i + 9 ))
        }
        return rows;
    }
    function generateColumns(rows){
        const columns = []
        for(let i = 0; i < 9; i++){
            columns.push([])
            for(let j = 0; j < 9; j++){
                (columns[i]).push(rows[j][i])
            }
        }
        return columns;
    }


    function generateSquares(sudoku){
        const sq = []
        for(let j = 0; j < 61; j = j + 27){
        for(let i = j; i < j + 8; i = i + 3){
            sq.push ((sudoku.slice(i, i +3).concat
            (sudoku.slice(i + 9, i + 12))).concat
            (sudoku.slice(i +18, i + 21)))
        }
        }
        return sq
    }

export function checkWin(sudoku) {
    const squares = generateSquares(sudoku)
    const rows = generateRows(sudoku)
    const columns = generateColumns(rows)

    const uniqueSquare = Array.from(new Set(columns[6]))
    console.log(uniqueSquare)

    const squareCheck = squares.every(sq => {
        const uniqueSquare = Array.from(new Set(sq))
        return uniqueSquare.length === 9
       } )
    const rowCheck = rows.every(row => {
        const uniqueRow = Array.from(new Set(row))
        return uniqueRow.length === 9
    })
    const columnCheck = columns.every(column => {
        const uniqueColumn = Array.from(new Set(column))
        return uniqueColumn.length === 9
    })

   if(squareCheck && rowCheck && columnCheck){
      return true
   }
   return false
}
