import React from 'react';
import Tile from './Tile';
import {nanoid} from "nanoid"
import Confetti from "react-confetti"
import Number from './Number'
import {generateSudokuGrid, allNumbers} from './SudokuApi'

export default function Game() {
    const [sudoku, setSudoku] = React.useState(generateSudokuGrid())
    const numbers = allNumbers()
    const [selectTileId, setSelectTileId] = React.useState()
    const [notesMode, setNotesMode] = React.useState(false)

    const sudokuGrid = sudoku.map(s =>{
        return(
            <Tile
                id={s.id}
                number = {s.number}
                r = {s.r}
                c = {s.c}
                isNotInitValue={s.isNotInitValue}
                fillTile = {() => setSelectTileId(s.id)}
                notesMode = {s.notesMode}
                note = {s.note}
            />)
        })
    const rowNumber = numbers.map(n => {
        return(<Number
                number={n.value}
                id={n.id}
                count={n.count}
                selectNumber= {() => fillTile(n.value)}
                />)
    })
    function fillTile(value){
        setSudoku(oldSudoku => oldSudoku.map(s => {
            return (s.id === selectTileId && s.isNotInitValue) ?
                {...s, number: value,  notesMode: notesMode, note : [...s.note, value] } : s
        }))
    }

    function switchNotesMode(){
        setNotesMode(old => !old)
    }

    return (
        <main>
            <div className= "sudoku-grid">
                {sudokuGrid}
            </div>
            <div className= "num-grid">
                {rowNumber}
            </div>
            <button onClick={switchNotesMode} >{notesMode ? "notes" : "write"}</button>
        </main>
    )
}
