import React, {Fragment, useRef, useEffect, useState} from 'react';
import {nanoid} from "nanoid"
import Game from './Game'
import {generateSchemeIdList} from './SudokuApi'

export default function Menu(){
    const [schemesIdList, setSchemesIdList] = useState(generateSchemeIdList())
    const [showSchemesIdList, setShowSchemesIdList] = useState(false)

    function selectedSudoku(selectId){
            return (
                <Game
                    schemeId = {selectId}
                    id = {nanoid()}
                />
            )
    }

    const schemesIdListJs= 
         schemesIdList.map(scheme => {
            return(
                <h4 onClick={() => selectedeSudoku(scheme.id)}>
                    {scheme.id}
                </h4>
            )
        })
    return(
        <Fragment>
         <h4 className="menu"> Menu </h4>
         <button className = 'continue' onClick ={() => setShowSchemesIdList(true)}>Play</button>
         {showSchemesIdList && {schemesIdListJs}}
        </Fragment>
    )
}
